import React , {useState} from 'react';
import emailjs from 'emailjs-com';

const Result = () => {
    return (
        <p className="success-message">Tu mensaje ha sido enviado correctamente. Nos pondremos en contacto pronto.</p>
    )
}
function ContactForm({props}) {
    const [ result,showresult ] = useState(false);
    const [email, setEmail] = useState({
        from: 'cesczerpa@gmail.com',
        to: 'cesczerpa@hotmail.com',
        subject: '',
        text: '',
        name:'',
        phone:'',
        email:'',
      });

      const handleChange = (event) => {
        setEmail({ ...email, [event.target.name]: event.target.value });
      };

      const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const transporter = nodemailer.createTransport({
                service: 'gmail',
                auth: {
                  user: 'cesczerpa@gmail.com',
                  pass: 'sqoparwfefwubdrs'
                }
            });
      
          const info = await transporter.sendMail({
            from: email.from,
            to: email.to,
            subject: email.subject,
            text: email.text
          });
          console.log('Message sent: %s', info.messageId);
          showresult(true);
        } catch (error) {
          console.error(error);
        }
      };
    
    setTimeout(() => {
        showresult(false);
    }, 5000);

    return (
        <form action="" onSubmit={handleSubmit}>
            <div className="rn-form-group">
                <input 
                type="text"
                name="name"
                placeholder="Nombre"
                required
                value={email.name}
                onChange={handleChange}
                />
            </div>

            <div className="rn-form-group">
                <input 
                type="email"
                name="email"
                placeholder="Email"
                required
                value={email.email}
                onChange={handleChange}
                />
            </div>

            <div className="rn-form-group">
                <input 
                type="text"
                name="phone"
                placeholder="TelÃ©fono"
                required
                value={email.phone}
                onChange={handleChange}
                />
            </div>

            <div className="rn-form-group">
                <input 
                type="text"
                name="subject"
                placeholder="Asunto"
                required
                value={email.subject}
                onChange={handleChange}
                />
            </div>
            
            <div className="rn-form-group">
                <textarea 
                name="text"
                placeholder="Mensaje"
                required
                value={email.text}
                onChange={handleChange}
                >
                </textarea>
            </div>

            <div className="rn-form-group">
                <button className="rn-button-style--2 btn-solid" type="submit" value="submit" name="submit" id="mc-embedded-subscribe">Contactar</button>
            </div> 

            <div className="rn-form-group">
                {result ? <Result /> : null}
            </div> 
        </form>
    )
}
export default ContactForm;
